import JSZip from 'jszip';
import { jsPDF } from 'jspdf';
import autoTable from 'jspdf-autotable';
import { db } from '../db';
import { version } from '@/data/app-info';
import { UniversalFinding } from '../types/finding';
import { ExportModel, ExportScope, BoundedAuditLogItem, PdfType } from '../types/export';
import { computeFunnelMetrics } from '../reporting/computeFunnelMetrics';

export class ReportingService {
    /**
     * PDF Exporter: Generates Executive or Engineer reports.
     */
    /**
     * PDF Exporter: Generates Executive or Engineer reports with seasoned design.
     */
    async exportPdfReport(model: ExportModel, type: PdfType): Promise<Blob> {
        const doc = new jsPDF();
        const metrics = model.funnelMetrics.currentView || model.funnelMetrics.session;
        const pageWidth = doc.internal.pageSize.getWidth();
        const pageHeight = doc.internal.pageSize.getHeight();

        // Brand Colors
        const BRAND_NAVY = [10, 10, 54] as [number, number, number]; // #0A0A36
        const BRAND_RED = [135, 26, 17] as [number, number, number]; // #871A11
        const SLATE_900 = [15, 23, 42] as [number, number, number];
        const SLATE_600 = [71, 85, 105] as [number, number, number];
        const SLATE_400 = [148, 163, 184] as [number, number, number];
        const SLATE_200 = [226, 232, 240] as [number, number, number];

        // --- Helper: Branding Elements ---
        const drawHeader = (pageTitle: string) => {
            // Black bar at top
            doc.setFillColor(0, 0, 0);
            doc.rect(0, 0, pageWidth, 25, 'F');

            // Logo Lockup Image
            try {
                // public/ARGUS_Lockup_Dark.png (Base64)
            const imgData = '';
                // High-resolution Lockup image replaces both logo and tagline text
                doc.addImage(imgData, 'PNG', 14, 6, 70, 14);
            } catch (e) {
                console.warn('Failed to draw logo', e);
                // Fallback to text if image fails
                doc.setFont('helvetica', 'bold');
                doc.setFontSize(22);
                doc.setTextColor(255, 255, 255);
                doc.text('ARGUS', 14, 15);
                
                doc.setFont('helvetica', 'normal');
                doc.setFontSize(9);
                doc.text('CONTEXT OVER COUNTS', 42, 14);
            }
            // Report Type Badge
            doc.setFontSize(10);
            const typeLabel = type === 'EXECUTIVE' ? 'EXECUTIVE SUMMARY' : 'ENGINEERING DETAIL';
            const badgeWidth = doc.getTextWidth(typeLabel) + 10;
            doc.setFillColor(60, 60, 60); // Light Grey capsule
            doc.roundedRect(pageWidth - badgeWidth - 14, 8, badgeWidth, 10, 2, 2, 'F');
            doc.setTextColor(255, 255, 255);
            doc.setFont('helvetica', 'bold');
            doc.text(typeLabel, pageWidth - badgeWidth - 9, 13, { baseline: 'middle' });
        };

        const drawFooter = (pageNum: number) => {
            // Black bar for footer
            doc.setFillColor(0, 0, 0);
            doc.rect(0, pageHeight - 15, pageWidth, 15, 'F');

            doc.setFont('helvetica', 'normal');
            doc.setFontSize(8);
            doc.setTextColor(255, 255, 255);
            doc.text(`Argus Security Intelligence v${model.argusVersion} | Confidential Intelligence Report`, 14, pageHeight - 6);
            doc.text(`Page ${pageNum}`, pageWidth - 25, pageHeight - 6);
        };

        // --- 1. COVER PAGE (Executive Only) ---
        if (type === 'EXECUTIVE') {
            // Pure White background for architectural feel
            doc.setFillColor(255, 255, 255);
            doc.rect(0, 0, pageWidth, pageHeight, 'F');

            drawHeader('COVER');

            // Hero Section
            doc.setFontSize(38);
            doc.setTextColor(0, 0, 0); // Black text
            doc.setFont('helvetica', 'bold');
            doc.text('Security Assessment', 14, 75);
            doc.text('Session Report', 14, 88);

            // Minimalist divider
            doc.setDrawColor(0, 0, 0);
            doc.setLineWidth(2.5);
            doc.line(14, 98, 70, 98);

            // Metadata Detail
            doc.setFont('helvetica', 'normal');
            doc.setFontSize(11);
            doc.setTextColor(...SLATE_600);
            doc.text(`Audit Signature: ${model.sessionId.slice(0, 8)}...${model.sessionId.slice(-8)}`, 14, 120);
            doc.text(`Infrastructure Scope: ${model.view.scope === 'SESSION' ? 'Full Environment' : 'Filtered Component View'}`, 14, 128);
            doc.text(`Generation Timestamp: ${new Date(model.exportedAt).toLocaleString()}`, 14, 136);

            // High-Value Summary Section
            doc.setFillColor(248, 250, 252); // slate-50 background for recap
            doc.setDrawColor(...SLATE_200);
            doc.setLineWidth(0.3);
            doc.roundedRect(14, 160, pageWidth - 28, 45, 1, 1, 'FD');

            doc.setFont('helvetica', 'bold');
            doc.setFontSize(10);
            doc.setTextColor(0, 0, 0);
            doc.text('EXECUTIVE RECAP', 20, 172);

            doc.setFont('helvetica', 'normal');
            doc.setTextColor(...SLATE_600);
            doc.setFontSize(10);
            const recap = `This document provides a condensed visibility profile of the target environment. By utilizing reachability telemetry and triage correlation, we identified ${metrics.actionableCount} high-density actionable risks against a total population of ${metrics.totalFindings} observations. The noise reduction baseline is calculated at ${metrics.noisePercent}%.`;
            doc.text(doc.splitTextToSize(recap, pageWidth - 40), 20, 180);

            drawFooter(1);
            doc.addPage();
        }

        // --- 2. DATA PAGE ---
        drawHeader(type);

        // Analysis Funnel Section
        doc.setFontSize(14);
        doc.setTextColor(0, 0, 0);
        doc.setFont('helvetica', 'bold');
        doc.text('Key Vulnerability Metrics', 14, 45);

        const metricsData = [
            ['Metric', 'Population', 'Contextual Significance'],
            ['Total Observations', metrics.totalFindings.toString(), 'Ingested population from primary source scan'],
            ['Vulnerable Population', metrics.vulnerableCount.toString(), 'High/Critical severity vulnerabilities identified'],
            ['Actionable (Reachable)', metrics.actionableCount.toString(), 'Confirmed reachable via Argus dependency analysis'],
            ['Calculated Toil (Noise)', `${metrics.noiseCount} (${metrics.noisePercent}%)`, 'False Positives, Accepted Risks, and Suppressions'],
            ['Remediation Ready', metrics.remediationReady.toString(), 'Validated fix suggestions available for deployment'],
        ];

        autoTable(doc, {
            startY: 52,
            head: [metricsData[0]],
            body: metricsData.slice(1),
            theme: 'striped',
            headStyles: {
                fillColor: [0, 0, 0], // Pure black header
                textColor: 255,
                fontSize: 9,
                fontStyle: 'bold',
                halign: 'left'
            },
            bodyStyles: {
                fontSize: 8.5,
                textColor: SLATE_900,
                cellPadding: 4
            },
            alternateRowStyles: {
                fillColor: [250, 251, 252] // Very light slate/gray
            },
            columnStyles: {
                1: { fontStyle: 'bold', textColor: [0, 0, 0] }
            },
            margin: { left: 14, right: 14 }
        });

        // 3. Detailed Findings (Engineer Only)
        if (type === 'ENGINEER') {
            const finalY = (doc as any).lastAutoTable.finalY + 20;

            doc.setFontSize(13);
            doc.setTextColor(0, 0, 0);
            doc.setFont('helvetica', 'bold');
            doc.text('Engineering Detail - Actionable Trace', 14, finalY);

            const findings = model.findings.slice(0, 2000);
            const tableData = findings.map(f => [
                f.id.split('-')[0], // Short ID
                f.severity.toUpperCase(),
                f.status,
                f.state?.resolution || 'ACTIVE',
                `${f.location.filepath}:${f.location.startLine || '?'}`
            ]);

            autoTable(doc, {
                startY: finalY + 7,
                head: [['ID', 'Severity', 'Status', 'Resolution', 'Trace / Location']],
                body: tableData,
                theme: 'grid',
                headStyles: {
                    fillColor: [0, 0, 0], // Black header for consistency
                    fontSize: 8,
                    textColor: 255
                },
                styles: {
                    fontSize: 7,
                    overflow: 'ellipsize',
                    cellPadding: 2,
                    lineColor: SLATE_200
                },
                columnStyles: {
                    0: { cellWidth: 15 },
                    1: { cellWidth: 20 },
                    4: { cellWidth: 'auto' }
                }
            });

            if (model.findings.length > 2000) {
                doc.setFontSize(8);
                doc.setTextColor(220, 38, 38); // red-600
                doc.text(`* Report truncated: Showing top 2,000 of ${model.findings.length} findings.`, 14, (doc as any).lastAutoTable.finalY + 10);
            }
        } else {
            // Priority Guidance for Executive
            const finalY = (doc as any).lastAutoTable.finalY + 15;
            doc.setFillColor(248, 250, 252); // slate-50
            doc.setDrawColor(...SLATE_200);
            doc.setLineWidth(0.3);
            doc.roundedRect(14, finalY, pageWidth - 28, 28, 1, 1, 'FD');

            doc.setFontSize(9);
            doc.setTextColor(0, 0, 0);
            doc.setFont('helvetica', 'bold');
            doc.text('REMEDIATION STRATEGY', 20, finalY + 10);

            doc.setFont('helvetica', 'normal');
            doc.setTextColor(...SLATE_600);
            doc.setFontSize(9);
            const priorityText = `Remediation effort should prioritize the ${metrics.remediationReady} "Remediation Ready" findings. These represent the highest ROI for security posture improvement, featuring validated fix suggestions and confirmed reachability. Contact your security team to initiate deployment.`;
            doc.text(doc.splitTextToSize(priorityText, pageWidth - 40), 20, finalY + 16);
        }

        drawFooter(doc.getNumberOfPages());

        return doc.output('blob');
    }

    /**
     * Bundle Exporter: Create a ZIP containing SARIF, Triage Overlay, Manifest and README.
     */
    async exportSarifBundle(model: ExportModel): Promise<Blob> {
        const zip = new JSZip();

        // 1. Fetch source SARIF (byte-preserving)
        const sarifArtifact = await db.session_artifacts
            .where('[sessionId+artifactType]')
            .equals([model.sessionId, 'SARIF'])
            .first();

        if (sarifArtifact?.content) {
            zip.file(sarifArtifact.filename || 'source.sarif', sarifArtifact.content);
        }

        // 2. Argus Triage Overlay
        const triageOverlay = JSON.stringify(model, null, 2);
        zip.file('argus-triage.json', triageOverlay);

        // 3. Manifest (Hashes + Versions)
        const manifest = {
            manifestVersion: '1.0',
            exportSchemaVersion: model.schemaVersion,
            metricsDefinitionVersion: model.funnelMetrics.session.metricsDefinitionVersion,
            argusVersion: model.argusVersion,
            exportedAt: model.exportedAt,
            files: {
                [sarifArtifact?.filename || 'source.sarif']: {
                    sha256: model.sourceSarif.sha256
                },
                'argus-triage.json': {
                    sha256: await this.computeHash(triageOverlay)
                }
            }
        };
        zip.file('manifest.json', JSON.stringify(manifest, null, 2));

        // 4. README.md
        const readme = `# Argus Export Bundle
This bundle contains security findings and triage state exported from Argus.

## Contents
- \`${sarifArtifact?.filename || 'source.sarif'}\`: Original SARIF findings (unmodified).
- \`argus-triage.json\`: Argus-specific triage state, metrics, and audit summary.
- \`manifest.json\`: Integrity hashes and schema versions.

## Verification
You can verify the integrity of the files using the SHA-256 hashes in \`manifest.json\`.

## Metrics Definition (metrics.v1)
- **Scope**: ${model.view.scope}
- **Vulnerable**: Counts findings with severity 'critical' or 'high'.
- **Actionable**: Counts findings where dependency analysis confirmed reachability ('REAL').
- **Noise**: Counts findings marked as False Positive, Accepted Risk, or Suppressed.
- **Remediation Ready**: Counts findings with an available fix suggestion.

---
Exported by Argus v${model.argusVersion} at ${new Date(model.exportedAt).toISOString()}
`;
        zip.file('README.md', readme);

        return await zip.generateAsync({ type: 'blob' });
    }

    /**
     * Assembler: Build the canonical ExportModel for a given session and scope.
     */
    async buildExportModel(
        sessionId: string,
        scope: ExportScope,
        viewState?: { filters: any; sort: any; findings?: UniversalFinding[] }
    ): Promise<ExportModel> {
        const now = Date.now();

        // 1. Fetch Master Session Data
        const session = await db.sessions.get(sessionId);
        if (!session) throw new Error(`Session ${sessionId} not found`);

        // 2. Resolve Populations
        // sessionFindings: Full session
        const sessionFindings = await db.findings
            .where('sessionId')
            .equals(sessionId)
            .toArray();

        // exportFindings: The population being exported
        const exportFindings = scope === 'SESSION'
            ? sessionFindings
            : (viewState?.findings || sessionFindings); // Simplified: Assume caller passes filtered findings for CURRENT_VIEW

        // 3. Build Fix Artifact Set (READY status)
        const fixArtifacts = await db.fix_suggestions
            .where('sessionId')
            .equals(sessionId)
            .filter(f => f.status === 'READY')
            .toArray();
        const fixArtifactIds = new Set(fixArtifacts.map(f => f.findingId));

        // 4. Compute Metrics (metrics.v1)
        const sessionMetrics = computeFunnelMetrics(sessionFindings, 'SESSION', fixArtifactIds);
        let viewMetrics;
        if (scope === 'CURRENT_VIEW') {
            viewMetrics = computeFunnelMetrics(exportFindings, 'CURRENT_VIEW', fixArtifactIds);
        }

        // 5. Assemble Bounded Audit Log (Last 5 events per exported finding)
        const auditSummary: Record<string, BoundedAuditLogItem[]> = {};
        for (const finding of exportFindings) {
            const events = await db.finding_events
                .where('findingId')
                .equals(finding.id)
                .reverse()
                .limit(5)
                .toArray();

            auditSummary[finding.id] = events.map(e => ({
                timestamp: e.timestamp,
                action: e.action,
                actor: e.actor,
                reason: e.reason,
                diff: e.diff
            }));
        }

        // 6. SARIF Metadata (Fetch original artifact)
        const sarifArtifact = await db.session_artifacts
            .where('[sessionId+artifactType]')
            .equals([sessionId, 'SARIF'])
            .first();

        if (!sarifArtifact) {
            console.warn('Original SARIF artifact not found for this session.');
        }

        // Hashing (SHA-256) via Web Crypto
        let sha256 = 'unknown';
        if (sarifArtifact?.content) {
            sha256 = await this.computeHash(sarifArtifact.content);
        }

        // 7. Final Model assembly
        const model: ExportModel = {
            schemaVersion: 'export.v1',
            sessionId,
            exportedAt: now,
            argusVersion: version,
            view: {
                scope,
                filters: viewState?.filters || {},
                sort: viewState?.sort || {}
            },
            funnelMetrics: {
                session: sessionMetrics,
                currentView: viewMetrics
            },
            findings: exportFindings,
            auditSummary,
            sourceSarif: {
                filename: sarifArtifact?.filename || 'source.sarif',
                sha256,
                artifactType: 'SARIF'
            }
        };

        return model;
    }

    private async computeHash(content: string | Blob): Promise<string> {
        try {
            const data = typeof content === 'string'
                ? new TextEncoder().encode(content)
                : await content.arrayBuffer();

            const hashBuffer = await crypto.subtle.digest('SHA-256', data);
            const hashArray = Array.from(new Uint8Array(hashBuffer));
            return hashArray.map(b => b.toString(16).padStart(2, '0')).join('');
        } catch (e) {
            console.error('Hashing failed', e);
            return 'error';
        }
    }
}

export const reportingService = new ReportingService();
